package com.namita.datatypes;

public class Airthmeticoperationprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("Airthmetic operation of two no");
		System.out.println("addition of two no.:"+(a+b));
		System.out.println("subtraction of two no:"+(b-a));
		System.out.println("multiplication of two no:"+(a*b));
		System.out.println("division of two no:"+(b/a));
		System.out.println("modulo of two no:"+(a%b));

	}

}
