package com.namita.datatypes;

public class Ifelseladderprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city="delhi";
		if(city=="meerut") {
			System.out.println("city is meerut");//if first condition is true
		}
		else if(city=="noida") {
			System.out.println("city is noida");// execute if second condition is true
		}
		else if(city=="agra") {
			System.out.println("city is agra");//execute if third condition is true
		}
			else {
				System.out.println("city is delhi");//execute when all condition is false
			
			}
		
		}
		

	}


